function onChange(control, oldValue, newValue, isLoading) {
   if (isLoading || newValue == '') {
      return;
   }


   //Type appropriate comment here, and begin script below

//    var eleve=g_form.getValue("eleve");
//    alert(eleve);
//    g_form.setValue("eleve",eleve);


if (g_form.getValue("raison")!="" && g_form.getValue("situation_familiale_de_l_l_ve")!="")
{
var ga = new GlideAjax('global.eleve_ajax_utils');
ga.addParam('sysparm_name', 'getclasse');
ga.addParam('eleve', g_form.getValue("eleve"));
ga.getXMLAnswer(Parse);


}
function Parse(response) {
    if (response != "78911a0d1b413950d991c882604bcbc2" && response != "74911a0d1b413950d991c882604bcbc3") {
		//eleve is not preschooler->hide
		g_form.setVisible("prealables_au_langage_ecrit", false);
		g_form.setMandatory("prealables_au_langage_ecrit", false);
		g_form.setVisible("quel_est_le_r_sultat_obtenu_au_dernier_d_pist_crit",false);
		g_form.setMandatory("quel_est_le_r_sultat_obtenu_au_dernier_d_pist_crit",false);
		g_form.setValue("quel_est_le_r_sultat_obtenu_au_dernier_d_pist_crit","");


		g_form.setVisible('langage_ecrit', true);
        g_form.setVisible('des_difficult_s_sont_elles_observ_es_au_niveau_de_la_lecture_ou_de_l_criture', true);
        g_form.setMandatory('des_difficult_s_sont_elles_observ_es_au_niveau_de_la_lecture_ou_de_l_criture', true);
    } else {
		//eleve is preschooler->show
		g_form.setVisible("prealables_au_langage_ecrit", true);
		g_form.setVisible("quel_est_le_r_sultat_obtenu_au_dernier_d_pist_crit",true);
		g_form.setMandatory("quel_est_le_r_sultat_obtenu_au_dernier_d_pist_crit",true);


		g_form.setVisible('langage_ecrit', false);
		g_form.setMandatory('langage_ecrit', false);
        g_form.setVisible('des_difficult_s_sont_elles_observ_es_au_niveau_de_la_lecture_ou_de_l_criture', false);
        g_form.setMandatory('des_difficult_s_sont_elles_observ_es_au_niveau_de_la_lecture_ou_de_l_criture', false);
		g_form.setValue('des_difficult_s_sont_elles_observ_es_au_niveau_de_la_lecture_ou_de_l_criture',"");

    }
}

