catalog_script_client.script:
function onSubmit() {
   //Type appropriate comment here, and begin script below
   if(g_form.getValue("est_ce_que_le_formulaire_d_aiguillage_services_orthophoniques") == 'no'){
	g_form.addErrorMessage("Vous devez compléter le formulaire d’aiguillage avant de soumettre la demande afin de vous assurer que l'élève répond aux critères d'admissibilité.")
	return false;
   }

   var sligr=new GlideRecord('x_ndecd_triage_2_0_tsa_cs');
	sligr.addQuery('eleve', g_form.getValue("eleve"));
	sligr.addQuery('etat_annee',1);
	sligr.query(checkeleveexiste);
	function checkeleveexiste(result){
	if (result.next()) {
        g_form.addErrorMessage("Une demande a déjà été acheminée, cette année pour cet élève."); //Veuillez acheminer un courriel au comité SLI-ROI afin de préciser les éléments que vous souhaitez mettre à jour
		//g_form.setMandatory('consentement_parental_verbal',false);
		//g_form.setVisible("consentement_parental_verbal",false);
		//g_form.setValue("consentement_parental_verbal","false");
	}

	}
   if(g_form.getValue("can_submit")=="false"){
		return false;
	}
}
