catalog_script_client.script:
function onChange(control, oldValue, newValue, isLoading) {
   if (isLoading || newValue == '') {
      return;
   }

   //Type appropriate comment here, and begin script below

  var today_date = new Date();
  var newDate = new Date (newValue);
  var defferenceDate = ((today_date-newDate)/86400000);
  if (today_date>newDate){
	if (defferenceDate> 365){
	g_form.showFieldMsg('date_du_rapport',"Veuillez noter que si le rapport orthophonique date de plus d’un an, les informations partagées pourraient ne plus refléter le profil actuel de l’élève. Si tel est le cas, veuillez choisir “consultation avec possibilité d’évaluation”.",'error');
	}

  }


}
