function onLoad() {
    //Type appropriate comment here, and begin script below
    var dte = new Date();
	var year = dte.getFullYear();
    var D_one = new Date(year, 11, 1, 0, 0, 0, 0); // 1er décembre
    var D_two = new Date(year, 9, 1, 0, 0, 0, 0); // 1er octobre
    var D_three = new Date(year, 4, 26, 0, 0, 0, 0); //26 mai)
    var D_four = new Date(year, 0, 1, 0, 0, 0, 0); //1er janvier

		// g_form.addOption('je_soumets_le_formulaire_de_consentement_pour', 'evaluation_sommaire_roi_avec_possibilite_de_bloc_dintervention_option_visible', 'Évaluation sommaire ROI avec possibilité de bloc d’intervention');

		// g_form.addOption('je_soumets_le_formulaire_de_consentement_pour', 'bloc_dintervention_roi_option_seulement_disponible_du_1_er_janvier_au_26_mai', "Bloc d’intervention ROI");

    if (((dte - D_two) > 0) && ((dte - D_one) < 0)) {
       g_form.addOption('je_soumets_le_formulaire_de_consentement_pour', 'evaluation_sommaire_roi_avec_possibilite_de_bloc_dintervention_option_visible', 'Évaluation sommaire ROI avec possibilité de bloc d’intervention');
   } else if (((dte - D_four) > 0) && ((dte - D_three) < 0)) {
       g_form.addOption('je_soumets_le_formulaire_de_consentement_pour', 'bloc_dintervention_roi_option_seulement_disponible_du_1_er_janvier_au_26_mai', "Bloc d’intervention ROI");
   }
    }
