catalog_script_client.script:
function onChange(control, oldValue, newValue, isLoading) {
    if (isLoading || newValue == '') {
        return;
    }
    //check if demande existe pour l'eleve
	var sligr=new GlideRecord('x_ndecd_triage_2_0_sli_roi');
	sligr.addQuery('l_ve', newValue);
	sligr.query(checkeleveexiste);
	function checkeleveexiste(result){
	if (result.next()) {
        g_form.showFieldMsg('l_ve',"Une demande a déjà été acheminée pour cet élève. Veuillez acheminer un courriel au comité SLI-ROI afin de préciser les éléments que vous souhaitez mettre à jour",'error');
		//g_form.setMandatory('consentement_parental_verbal',false);
		//g_form.setVisible("consentement_parental_verbal",false);
		//g_form.setValue("consentement_parental_verbal","false");
		g_form.setValue("can_submit","false");
	}else{
		///g_form.showFieldMsg('l_ve',"Ce programme vise la stimulation des habiletés langagières, telles que la compréhension des consignes/concepts, les structures de phrases, le vocabulaire, le discours ainsi que les préalables au langage écrit tels que la conscience phonologique et l'apprentissage des lettres de l'alphabet. Pour y être admissible, un élève doit présenter de telles difficultés dans sa langue dominante, c'est-à-dire la langue dans laquelle il ou elle est le plus confortable pour s'exprimer. Il est à noter que les élèves présentant seulement des difficultés au niveau de la parole (ex.: articulation des sons, bégaiement) ou au niveau de la lecture/écriture ne sont pas admissibles",'info');
		//g_form.setMandatory('consentement_parental_verbal',true);
		//g_form.setVisible("consentement_parental_verbal",true);
		g_form.setValue("can_submit","true");
	}

	}



    var school;
    //Type appropriate comment here, and begin script below
    var now_GR = new GlideRecord('x_ndecd_triage_2_0_eleve');
    now_GR.addQuery('sys_id', newValue); // Priority is 4 – Low and,
    now_GR.query(response);

    function response(result) {
        if (result.next()) {
            school = result.getValue('u_nom_de_l_cole');
            var admins = new GlideRecord('x_ndecd_triage_2_0_x_ndecd_school');
            admins.addQuery('sys_id', school); // Priority is 4 – Low and,
            admins.query(responseA);


        }
    }

    function responseA(result) {
        while (result.next()) {
            var u_admin = result.getValue('admins');
            g_form.setValue("direction", u_admin);

        }
    }


    // var school;
    // school = gselve.getValue('u_nom_de_l_ecole');

    // var gr = new GlideRecord('');




}
