sys_script.script:
(function executeRule(current, previous /*null when async*/ ) {
    var html;
    var newHtml;

    var gr = new GlideRecord('x_ndecd_triage_2_0_pdftemplate');
    gr.addQuery('template_name', 'PDFcon-eva-req-oth');
    gr.query();
    while (gr.next()) {
        html = gr.html_template;
    }
    var hfInfo = new Object();
    hfInfo["HeaderImageAttachmentId"] = "fe71ca801b04b910d991c882604bcb30";
    hfInfo["HeaderImageAlignment"] = "left";
    hfInfo["FooterImageAttachmentId"] = "f69aa9661bf7a5108d594047bc4bcb1c";
    hfInfo["FooterImageAlignment"] = "TOP_CENTER";
    hfInfo["PageSize"] = "A4";
    hfInfo["GeneratePageNumber"] = "false";
    hfInfo["TopOrBottomMargin"] = "50";
    hfInfo["LeftOrRightMargin"] = "24";
    // Add your code here
    var targetTableSysId = current.sys_id;
    var tableName = 'x_ndecd_triage_2_0_envoi_d_un_formulaire_de_consentement_pour_le_programme_roi';
    var pdfName = "Consentement ROI " + current.getDisplayValue('eleve');
	gs.info("vvvvvvvvvvvv"+html);
    //information eleve
	newHtml = html.replace("var1", current.getDisplayValue('eleve'));
	var gselve = new GlideRecord('x_ndecd_triage_2_0_eleve');
    gselve.get(current.getValue("eleve"));
    newHtml = newHtml.replace("var2",gselve.u_date_de_naissance );
    newHtml = newHtml.replace("var3",gselve.u_niso );
    newHtml = newHtml.replace("var4", gselve.getDisplayValue('u_nom_de_l_cole'));
    newHtml = newHtml.replace("var5",  gselve.getDisplayValue('u_classe'));
	//information direction
	newHtml = newHtml.replace("var90", current.eleve.u_nom_de_l_cole.admins.getDisplayValue());

	record_fields={
		"var6":"est_ce_que_l_l_ve_a_un_diagnostic",
		"var7":"situation_familiale"
	}

	for (const property in record_fields) {

		newHtml = newHtml.replace(property, current.getDisplayValue(record_fields[property])?current.getDisplayValue(record_fields[property]):"Sans objet");
    }

	newHtml = newHtml.replace("Autre situation familiale:", current.getDisplayValue("autre_situation")?"Autre situation familiale:":"");
	newHtml = newHtml.replace("var8", current.getDisplayValue("autre_situation")?current.getDisplayValue("autre_situation"):"");
	//information opened by
	newHtml = newHtml.replace("var60", current.getDisplayValue('opened_by'));
    newHtml = newHtml.replace("var62", current.getDisplayValue('opened_by'));
    newHtml = newHtml.replace("var63", current.getDisplayValue('sys_created_on'));


	var user =  new GlideRecord('sys_user');
	user.addEncodedQuery('name='+current.getDisplayValue('opened_by'));
	user.query();
	if(user.next()){
	newHtml = newHtml.replace("var61", user.getDisplayValue('title'));
	}

    var v = new sn_pdfgeneratorutils.PDFGenerationAPI;
    var result = v.convertToPDFWithHeaderFooter(newHtml, tableName, targetTableSysId, pdfName, hfInfo);
    current.linkattachment = result.attachment_id;
    current.update();
})(current, previous);
