sys_script.script:
(function executeRule(current, previous /*null when async*/) {
var score_roi =0 ;
var ld = current.langue_dominante;
var scoreld = 0;
if (ld == 'french'){
	scoreld = 2;
}

var lr = current.degr_de_s_v_rit_langage_r_ceptif;
var scoreLR = 0;

var le = current.degr_de_s_v_rit_langage_expressif;
var scoreLE = 0;

var cp = current.c_te_de_priorisation_de_base;
var scorecp = 151 * (parseInt(cp) + 1 );

var nScolaire = current.eleve.u_classe;
var scorenScolaire = 0;
if (nScolaire == '74911a0d1b413950d991c882604bcb59'){
scorenScolaire = 1;
}

// french severe severe 3 74911 a0d1b413950d991c882604bcb59
//scoreRoi  = CP*17 + LR*8 + LE*5  + LD*3 + NS
lr = lr.toString();

switch(lr){
case 'severe':
scoreLR = 144;
break;
case 'modere-a-severe':
scoreLR = 120;
break;
case 'modere':
scoreLR = 96;
break;
case 'leger-a-modere':
scoreLR = 72;
break;
case 'leger':
scoreLR = 48;
break;
case 'adequat':
scoreLR = 24;
break;

}

le = le.toString();

switch(le){
case 'severe':
scoreLE = 24;
break;
case 'modere-a-severe':
scoreLE = 20;
break;
case 'modere':
scoreLE = 16;
break;
case 'leger-a-modere':
scoreLE = 12;
break;
case 'leger':
scoreLE = 8;
break;
case 'adequat':
scoreLE = 4;
break;

}
score_roi = scorecp + scoreLE + scoreLR + scoreld + scorenScolaire;
current.score_roi = score_roi;
current.update();

})(current, previous);
