sys_script.script:
(function executeRule(current, previous /*null when async*/) {

	// Get the current date
var currentDate = new GlideDate();

// Check if the current date's month and day are between September 15th and December 1st
var isBetweensepdec = (currentDate.getMonthNoTZ() == 9 && currentDate.getDayOfMonthNoTZ() >= 15) ||
                (currentDate.getMonthNoTZ() > 9 && currentDate.getMonthNoTZ() < 12) ||
                (currentDate.getMonthNoTZ() == 12 && currentDate.getDayOfMonthNoTZ() <= 1);

if (isBetweensepdec) {
    current.sendable_15_sep_1er_dec="yes";
} else {
    current.sendable_15_sep_1er_dec="no";
}
current.update();

var isBetweenjanmay = (currentDate.getMonthNoTZ() == 1 && currentDate.getDayOfMonthNoTZ() >= 1) ||
                (currentDate.getMonthNoTZ() > 1 && currentDate.getMonthNoTZ() < 5) ||
                (currentDate.getMonthNoTZ() == 5 && currentDate.getDayOfMonthNoTZ() <= 1);

if (isBetweenjanmay) {
    current.sendable_1er_jan_1er_mai="yes";
} else {
    current.sendable_1er_jan_1er_mai="no";
}
current.update();


})(current, previous);
