sys_script.script:
(function executeRule(current, previous /*null when async*/ ) {
    gs.info(JSON.stringify(current));
    var html;
    var newHtml;
    var gr = new GlideRecord('x_ndecd_triage_2_0_pdftemplate');
    gr.addQuery('template_name', 'Demande de ROI');
    gr.query();
    while (gr.next()) {
        html = gr.html_template;
    }
    var hfInfo = new Object();
    hfInfo["HeaderImageAttachmentId"] = "fe71ca801b04b910d991c882604bcb30";
    hfInfo["HeaderImageAlignment"] = "left";
    hfInfo["FooterImageAttachmentId"] = "f69aa9661bf7a5108d594047bc4bcb1c";
    hfInfo["FooterImageAlignment"] = "TOP_CENTER";
    hfInfo["PageSize"] = "A4";
    hfInfo["GeneratePageNumber"] = "false";
    hfInfo["TopOrBottomMargin"] = "50";
    hfInfo["LeftOrRightMargin"] = "24";
    // Add your code here
    var targetTableSysId = current.sys_id;
    var tableName = 'x_ndecd_triage_2_0_envoi_d_un_formulaire_de_consentement_pour_le_programme_roi';
    var pdfName = "Envoi d’un formulaire de consentement pour le programme ROI pour " + current.getDisplayValue('eleve');
    newHtml = html.replace("var1", current.getDisplayValue('eleve'));

    if (current.getDisplayValue('autre_situation') != "") {
        newHtml = newHtml.replace("var6", current.getDisplayValue('autre_situation'));
    } else {
        newHtml = newHtml.replace('<p><span style="font-size: 10pt;">Autre situation explication: <strong>var6</strong></span></p>', '');
    }

	// //added 2 missing question. to verify
	// newHtml = newHtml.replace("var2", current.getDisplayValue('est_ce_que_l_l_ve_fr_quente_pr_sentement_une_classe_distincte')); 
    //     if (current.getValue('classe_distinct') != null) {
    //     newHtml = newHtml.replace("var3", current.getDisplayValue('classe_distinct'));
    // } else {
    //     newHtml = newHtml.replace('<div><span style="font-size: 10pt;">Classe distincte: <strong>var3</strong></span></div>', '');
    // }


    newHtml = newHtml.replace("var9", current.getDisplayValue('raison_de_soumissions_de_formulaire'));
    newHtml = newHtml.replace("var8", current.getDisplayValue('date_du_courriel'));
    newHtml = newHtml.replace("var7", current.getDisplayValue('nom_du_demandeur'));
    newHtml = newHtml.replace("var5", current.getDisplayValue('situation_familiale'));
    newHtml = newHtml.replace("var4", current.getDisplayValue('est_ce_que_l_l_ve_a_un_diagnostic'));
    newHtml = newHtml.replace("var20", current.getDisplayValue('opened_by'));
    newHtml = newHtml.replace("var20", current.getDisplayValue('opened_by'));
    newHtml = newHtml.replace("var21", current.getDisplayValue('sys_created_on'));

    var dir = current.number;
    var gsdirect = new GlideRecord('sysapproval_approver');
    gsdirect.addEncodedQuery('sysapproval.number=' + dir + '^state=approved');
    gsdirect.query();
    while (gsdirect.next()) {
        newHtml = newHtml.replace("var90", gsdirect.getDisplayValue('approver'));
        newHtml = newHtml.replace("var91", gsdirect.getDisplayValue('approver'));
    }
	var approver =  new GlideRecord('sys_user');
	approver.addEncodedQuery('name='+gsdirect.getDisplayValue('approver'));
	approver.query();
	if(approver.next()){
	newHtml = newHtml.replace("var22", approver.getDisplayValue('title'));

	}


    newHtml = newHtml.replace("var92", current.getDisplayValue('sys_updated_on'));
    var eleve1 = current.getValue("eleve");

    var gselve = new GlideRecord('sys_user');
    gselve.get(eleve1);
	newHtml = newHtml.replace("var14", gselve.getDisplayValue('u_classe'));
    newHtml = newHtml.replace("var13", gselve.getDisplayValue('u_nom_de_l_ecole'));
    newHtml = newHtml.replace("var12", gselve.u_niso);
    newHtml = newHtml.replace("var11", gselve.u_date_de_naissance);

    var v = new sn_pdfgeneratorutils.PDFGenerationAPI;
    var result = v.convertToPDFWithHeaderFooter(newHtml, tableName, targetTableSysId, pdfName, hfInfo);

    current.linkattachment = "/sys_attachment.do?sys_id=" + result.attachment_id;
    current.update();
})(current, previous);
