sys_script.script:
(function executeRule(current, previous /*null when async*/) {

	var currentYear = new Date().getFullYear();
	if (current.ann_e_de_la_demande==currentYear){
		current.etat_annee=1;

	}
	if (current.ann_e_de_la_demande==currentYear-1){
		current.etat_annee=0;

	}
	if (current.ann_e_de_la_demande==currentYear-2){
		current.etat_annee=10;

	}
	if (current.ann_e_de_la_demande==currentYear+1){
		current.etat_annee=2;

	}  
	if (current.ann_e_de_la_demande==currentYear && current.eleve.u_classe=="78911a0d1b413950d991c882604bcbc2"){//jk eleve
		current.etat_annee=3;
	}
	if (current.ann_e_de_la_demande==currentYear-1 && current.eleve.u_classe=="78911a0d1b413950d991c882604bcbc2"){//jk eleve
		current.etat_annee=4;
	}
	if (current.ann_e_de_la_demande==currentYear-2 && current.eleve.u_classe=="78911a0d1b413950d991c882604bcbc2"){//jk eleve
		current.etat_annee=5;
	}
	if (current.ann_e_de_la_demande==currentYear-2 && current.eleve.u_classe=="78911a0d1b413950d991c882604bcbc2"){//jk eleve
		current.etat_annee=6;
	}
	// Add your code here
	current.update();

})(current, previous);
