	gs.info("bbbbbbbbbbbbbbbb"+current.ann_e_de_la_demande_int)
	if (current.ann_e_de_la_demande_int==currentYear){
		current.etat_annee=1;
		current.update();
	}
	if (current.ann_e_de_la_demande_int==currentYear-1){
		current.etat_annee=0;
		current.update();
	}
	if (current.ann_e_de_la_demande_int==currentYear-2){
		current.etat_annee=10;
		current.update();
	}
	if (current.ann_e_de_la_demande_int==currentYear+1){
		current.etat_annee=2;
		current.update();
	}  
	if (current.ann_e_de_la_demande_int==currentYear && current.eleve.u_classe=="78911a0d1b413950d991c882604bcbc3"){//sk eleve
		current.etat_annee=3;
		current.update();
	}
	if (current.ann_e_de_la_demande_int==currentYear-1 && current.eleve.u_classe=="78911a0d1b413950d991c882604bcbc3"){//sk eleve
		current.etat_annee=4;
		current.update();
	}
	if (current.ann_e_de_la_demande_int==currentYear-2 && current.eleve.u_classe=="78911a0d1b413950d991c882604bcbc3"){//sk eleve
		current.etat_annee=5;
		current.update();
	}
	if (current.ann_e_de_la_demande_int==currentYear-3 && current.eleve.u_classe=="78911a0d1b413950d991c882604bcbc3"){//sk eleve
		current.etat_annee=6;
		current.update();
	}
	// Add your code here


})(current, previous);
